import React, {useState} from "react";

import "./expenseForm.css";

const ExpenseForm = () => {
    const [enteredTitle, setEnteredTitle] = useState("");
    const [enteredAmount, setEnteredAmount] = useState("");
    const [enteredDatee, setEnteredDate] = useState("");

    const titleChangeHandler = (event) => {
        setEnteredTitle(event.target.value);                       //kad napisemo bilo sta u title delu i pogledamo sta nam ispise u konzoli browsera, mozemo da vidimo kad kliknemo na target:input da tu ima brda opcija, odakle smo mi uzeli value; kad onda stavimo events.target.value, u konzoli ce nam ispisati tacno sto napisemo i u nasoj aplikaciji (nece vise ispisivati kao do sada 1x clicked!, 2x clicked! itd) 
    }
    const amountChangeHandler = (event) => {
        setEnteredAmount(event.target.value);
    }
    const dateChangeHandler = (event) => {
        setEnteredDate(event.target.value);
    }

    return (
    <form>
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label>Title</label>
          <input type="text" onChange={titleChangeHandler} />
        </div>
        <div className="new-expense__control">
          <label>Amount</label>
          <input type="number" min="0.01" step="0.01" onChange={amountChangeHandler}/>
        </div>
        <div className="new-expense__control">
          <label>Date</label>
          <input type="date" min="2019-01-01" max="2022-12-31" onChange={dateChangeHandler}/>
        </div>
        <div className="new-expense__actions">
          <button type="submit">Add Expense</button>
        </div>
      </div>
    </form>
  );
};

export default ExpenseForm;
//input tagove u React pisemo kao samozatvarajuce, posto medju otvarajucim i zatvarajucim tagom nema content-a; kod input broja, min="0.01" step="0.01" -> default html atributi za input elemente koji kontrolisu kako se ti elementi mogu koristiti
//isto vazi i kod input datuma, gde smo stavili da mogu da se koriste samo godine 2019, 2020, 2021 i 2022
