import React from "react";

import ExpenseForm from "./expenseForm";
import "./newExpense.css"

const NewExpense = (props) => {                                      //ovaj props uzima podatke od App.js
    const SaveExpenseDataHandler = (enteredExpenseData) => {         //enteredExpenseData je argument koji ova funkcija ocekuje da ce dobiti (ime nije default, sami smo stavili)
        const expenseData = {                                        //pravimo novi objekat
            ...enteredExpenseData,                                   //koji povlaci sve key-eve is expenseData objekta (od reda 23 do 26)
            id: Math.random().toString()                             //i dodaje novi key id, koji ce nasumicno da daje neki id i pretvori ga u string
        };
        props.onAddExpense(expenseData)                              //OVDE SALJEMO OD DETETA KA RODITELJU TAKODJE, OVOG PUTA U APP.JS (LIFTING UP KONCEPT)
        console.log(expenseData);                                    //u konzoli ce sad biti iz ovog fajla log umesto iz expenseForm.js kao sto je bilo do sada
    }                                                                //u redu 15 gde prosledjujemo podatke u ExpenseForm, smo takodje dodali onSaveExpenseData={}, koja ce imati kao vrednost neku funkciju, koja ce se pozvati unutar ExpenseForm komponente; ta funkcija (koja je opisana od reda 7 do 12) ce se znaci pozvati unutar tog objekta i dati mu vrednost, pa smo na taj nacin IZVUKLI PODATKE OD DETETA KA RODITELJU; takodje nema potrebe da onSaveExpenseData pocinje sa on, ali smo tako stavili kako bi nama bilo prepoznatljivije
    return <div className="new-expense">
        <ExpenseForm onSaveExpenseData={SaveExpenseDataHandler}/>                
        <form></form>
    </div>
}

export default NewExpense;
