import Expenses from "./components/expenses"; //posto sad saljemo u expenses.js, zakomentarisali smo gornji deo i importujemo Expenses funkciju iz tog fajla gde prosledjujemo dalje kod, koji cemo tamo preuzeti kroz props

function App() {
  //ovde mozemo napraviti atribut koji ce kroz props biti povucen u nase komponente
  /* const expenses = [
    {title: "Car Insurance", amount: 294.67, date: new Date(2021, 2, 28)},
    {title: "Car Insurance", amount: 294.67, date: new Date(2021, 2, 28)},
    {title: "Car Insurance", amount: 294.67, date: new Date(2021, 2, 28)},
    {title: "Car Insurance", amount: 294.67, date: new Date(2021, 2, 28)}
  ];
 */
  //gornji deo je zakomentarisan da ubacimo malo razlicitije atribute
  const expenses = [
    {
      id: "e1",
      title: "Toilet Paper",
      amount: 94.12,
      date: new Date(2020, 7, 14),
    },
    { id: "e2", title: "New TV", amount: 799.49, date: new Date(2021, 2, 12) },
    {
      id: "e3",
      title: "Car Insurance",
      amount: 294.67,
      date: new Date(2021, 2, 28),
    },
    {
      id: "e4",
      title: "New Desk (Wooden)",
      amount: 450,
      date: new Date(2021, 5, 12),
    },
  ];
  /*   const para = document.createElement('p');           ovako bi bilo normalnim javascript kodom, ali da ne bi pisali stalno takve kodove, React to skrati pa mozemo da pisemo kao u nastavku
  para. textContent = "This is also visible!";
  document.getElementById('root').append(para); */
  return (
    <div>
      <h2>Let's get started!</h2>
      {/* 
      <ExpenseItem
        title={expenses[0].title}
        amount={expenses[0].amount}
        date={expenses[0].date}
      ></ExpenseItem>
      <ExpenseItem
        title={expenses[1].title}
        amount={expenses[1].amount}
        date={expenses[1].date}
      ></ExpenseItem>
      <ExpenseItem
        title={expenses[2].title}
        amount={expenses[2].amount}
        date={expenses[2].date}
      ></ExpenseItem>
      <ExpenseItem
        title={expenses[3].title}
        amount={expenses[3].amount}
        date={expenses[3].date}
      ></ExpenseItem>*/}
      <Expenses items={expenses}></Expenses>
      <p>This is also visible!</p>
    </div>
  );
}

export default App; //da bi fajl mogao da se importuje, treba da se exportuje ovako
