import React, {useState} from "react";

import "./expenses.css";
import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";                      //ovo izmenjuje spoljni wrapper
import ExpensesFilter from "./ExpensesFilter";

//ovde za razliku od App.js, mora da povuce podatke preko props iz App.js a zatim da ih prosledi u ExpenseItem.js, zbog cega u atribute dodajemo props (u App kad smo na pocetku prosledjivali nije bilo props kod atributa)
function Expenses(props) {
  const [filteredYear, setFilteredYear] = use("2020")
  const filterChangeHandler = selectedYear => {
    //console.log("expenses.js");
    //console.log(selectedYear);
    setFilteredYear(selectedYear);
  }
  return (
   <div>
    <Card className="expenses">
    <ExpensesFilter onChangeFilter={filterChangeHandler}/>
      <ExpenseItem
        title={props.items[0].title}            //preimenovali smo pocetni props.expenses u props.items, pa smo menjali to i u App.js da bude preglednije jer se expenses ponavlja mnogo puta
        amount={props.items[0].amount}
        date={props.items[0].date}
      ></ExpenseItem>
      <ExpenseItem
        title={props.items[1].title}
        amount={props.items[1].amount}
        date={props.items[1].date}
      ></ExpenseItem>
      <ExpenseItem
        title={props.items[2].title}
        amount={props.items[2].amount}
        date={props.items[2].date}
      ></ExpenseItem>
      <ExpenseItem
        title={props.items[3].title}
        amount={props.items[3].amount}
        date={props.items[3].date}
      ></ExpenseItem>
    </Card>
    </div>
  );
}

export default Expenses;                        //prosledjujemo funkciju Expenses
