    event.preventDefault();
    if(enteredValue.trim().length === 0) {                  //ovim i sledecim redom onemogucavamo izbacivanje ljubicastog div tj pravouganika kad ne ukucamo nista
      useState(false);
      eturn;
    }
    props.onAddGoal(enteredValue);
  };

  return (
    <form onSubmit={formSubmitHandler}>
