import React, { useState } from 'react';

import Button from '../../UI/Button/Button';
import './CourseInput.css';

const CourseInput = props => {
  const [enteredValue, setEnteredValue] = useState('');
  const [isValid, setIsValid] = useState(true);             //krecemo od toga da smo uneli nesto validno

  const goalInputChangeHandler = event => {
    setEnteredValue(event.target.value);
  };

  const formSubmitHandler = event => {
    event.preventDefault();
    if(enteredValue.trim().length === 0) {                  //ovim i sledecim redom onemogucavamo izbacivanje ljubicastog div tj pravouganika kad ne ukucamo nista
      useState(false);
      eturn;
    }
    props.onAddGoal(enteredValue);
  };

  return (                                                  //{color: !isValid ? "red" : "black"} daje crvenu boju ako smo ukucali nesto nevalidno, u suprotnom crna boja
    <form onSubmit={formSubmitHandler}>
      <div className="form-control">
        <label style={{color: !isValid ? "red" : "black"}}>Course Goal</label>
        <input style={{borderColor: !isValid ? "red" : "black"}} type="text" onChange={goalInputChangeHandler} />
      </div>
      <Button type="submit">Add Goal</Button>
    </form>
  );
};

export default CourseInput;
